import React, { useEffect } from "react";
import "./About.css";
import Navbar from "./Navbar";
import gsap from "gsap";

const About = () => {
  useEffect(() => {
    // Animations for the hero and second section
    gsap.from(".hero-text-content", {
      opacity: 0,
      y: 50,
      duration: 1.5,
      ease: "power2.out",
    });
    gsap.from(".second-section-content", {
      opacity: 0,
      y: 50,
      duration: 1.5,
      ease: "power2.out",
      delay: 0.5,
    });
  }, []);

  return (
    <>
      {/* Hero Section */}
      <header className="hero-section">
        <Navbar />
        <div className="hero-image-container">
          <img
            src="/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/6.png" // Update with your actual path
            alt="Hero Background"
            className="hero-image"
          />
        </div>
        <div className="hero-text-content">
          <p className="hero-intro">Hi, I'M</p>
          <h1 className="hero-title">Deepa Devangmath</h1>
        </div>
      </header>

      {/* Main Content */}
      <main className="main-content">
        <section className="second-section">
          <div className="second-section-image-container">
            <img
              src="/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/3.JPG" // Update with your actual path
              alt="Deepa Devangmath"
              className="second-section-image"
            />
          </div>
          <div className="second-section-content">
            <p>
              I'm a recent architecture graduate with a deep passion for
              designing spaces that connect people and evoke emotion. To me,
              architecture is about more than just structures—it's about
              crafting environments that resonate with the way people live,
              feel, and interact.
            </p>
            <p>
              I believe in creating spaces that inspire and bring out the best
              in those who experience them. Eager to learn and adapt, I’ve
              always been quick to grasp new concepts and apply them
              effectively. I’m excited to explore new ideas, challenge
              conventions, and contribute to projects that make a meaningful
              impact on how we connect with the world around us.
            </p>
          </div>
        </section>
      </main>
    </>
  );
};

export default About;
