import React, { useEffect } from "react";
import "./About.css";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

const About = () => {
  useEffect(() => {
    gsap
      .timeline({
        scrollTrigger: {
          trigger: ".wrapper",
          start: "top top",
          end: "+=150%", // Adjust this to fit your content
          pin: true,
          scrub: true,
          markers: false, // Disable debug markers
          anticipatePin: 1, // Helps prevent glitching
        },
      })
      .to("img", {
        scale: 2,
        z: 350,
        transformOrigin: "center center",
        ease: "power1.inOut",
      })
      .to(
        ".section.hero",
        {
          scale: 1.1,
          transformOrigin: "center center",
          ease: "power1.inOut",
        },
        "<"
      );
  }, []);

  return (
    <div className="about-page">
      {/* Navigation */}
      <header className="header">
        <div className="copyright">Â© Arc Deepa Devangmath</div>
        <nav>
          <ul className="nav-links">
            <li><a href="#about">About</a></li>
            <li><a href="#projects">Projects</a></li>
            <li><a href="#contact">Contact</a></li>
          </ul>
        </nav>
      </header>

      {/* Wrapper with Sections */}
      <div className="wrapper">
        <div className="content">
          <section className="section hero"></section>
          <section className="section gradient-purple"></section>
          <section className="section gradient-blue"></section>
        </div>
        <div className="image-container">
          <img
            src="https://assets-global.website-files.com/63ec206c5542613e2e5aa784/643312a6bc4ac122fc4e3afa_main%20home.webp"
            alt="Modern architectural building"
          />
        </div>
      </div>
    </div>
  );
};

export default About;
