import React, { useEffect, useRef, useState } from "react";
import Navbar from "./Navbar";
import "./Projects.css";
import { motion, useScroll, useTransform } from "framer-motion";
import Image from "next/image";

// Project Images
import project1 from "/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/11.png";
import project2 from "/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/10.png";
import project3 from "/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/13.png";

const Projects = () => {
    const container = useRef(null);
    const { scrollYProgress } = useScroll({
        target: container,
        offset: ["start start", "end end"]
    });

    const scales = [
        useTransform(scrollYProgress, [0, 1], [1, 4]),
        useTransform(scrollYProgress, [0, 1], [1, 3.5]),
        useTransform(scrollYProgress, [0, 1], [1, 3]),
    ];

    const pictures = [
        { src: project1, scale: scales[0] },
        { src: project2, scale: scales[1] },
        { src: project3, scale: scales[2] },
    ];

    return (
        <>
            {/* Navbar */}
            <Navbar />

            {/* Sticky Scaling Images Section */}
            <div ref={container} className="sticky-container">
                <div className="sticky-wrapper">
                    {pictures.map(({ src, scale }, index) => (
                        <motion.div key={index} style={{ scale }} className="sticky-element">
                            <div className="sticky-image-container">
                                <Image src={src} alt={`Project ${index + 1}`} fill />
                            </div>
                        </motion.div>
                    ))}
                </div>
            </div>

            {/* Existing Hero Section */}
            <div className="project-hero">
                <div className="project-hero-content">
                    <div className="project-hero-year">
                        <p>2019</p>
                        <div className="line"></div>
                        <p>2024</p>
                    </div>
                    <div className="project-hero-title">
                        <h1>PROJECTS</h1>
                        <p>of Deepa Devangmath</p>
                    </div>
                </div>
            </div>

            {/* Existing Projects Section */}
            <div className="table-of-contents-section">
                <h1>Recent</h1>
                <h2>Work</h2>
                <p>Click to view projects</p>
                <div className="contents-list">
                    {[{ id: "01", img: project1, title: "Formula One Grand Prix", subtitle: "Masters Project", link: "/pro1" },
                    { id: "02", img: project2, title: "Urban Campus Housing", subtitle: "Masters Project", link: "/pro2" }].map(project => (
                        <a href={project.link} key={project.id} className="content-item">
                            <div className="content-image">
                                <img src={project.img} alt={project.title} />
                            </div>
                            <div className="content-details">
                                <span>{project.id}</span>
                                <h3>{project.title}</h3>
                                <p>{project.subtitle}</p>
                            </div>
                        </a>
                    ))}
                </div>
            </div>
        </>
    );
};

export default Projects;
