import React, { useEffect } from "react";
import Navbar from "./Navbar";
import "./About.css";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

const About = () => {
  useEffect(() => {
    // Hero Section Animation
    gsap.fromTo(
      ".hero-title",
      { opacity: 0, y: -50 },
      { opacity: 1, y: 0, duration: 1, ease: "power2.out" }
    );
    gsap.fromTo(
      ".hero-subtitle",
      { opacity: 0, y: 50 },
      { opacity: 1, y: 0, duration: 1, ease: "power2.out", delay: 0.5 }
    );

    // Parallax Effect
    gsap.to(".hero", {
      backgroundPositionY: "50%",
      scrollTrigger: {
        trigger: ".hero",
        start: "top top",
        end: "bottom top",
        scrub: true,
      },
    });

    // Introduction Section Animation
    gsap.fromTo(
      "#introduction",
      { opacity: 0, y: 50 },
      {
        opacity: 1,
        y: 0,
        duration: 1,
        ease: "power2.out",
        scrollTrigger: {
          trigger: "#introduction",
          start: "top 80%",
          end: "top 60%",
        },
      }
    );
  }, []);

  return (
    <>
      {/* Hero Section */}
      <section className="hero">
        <Navbar />
        <header className="hero-header">
          <h1 className="hero-title">Deepa Devangmath</h1>
          <p className="hero-subtitle">Architect | Urban Designer | Interior Specialist</p>
        </header>
      </section>

      {/* Main Content */}
      <article id="introduction">
        <h2>Introduction</h2>
        <p>
          I am a licensed architect from India, currently in my ninth year of studying architecture. My
          research delves into how space impacts human behavior and, in turn, their mental well-being. My
          design philosophy centers on creating harmony between context, built form, and users. I have actively
          collaborated on various live projects and take great interest in conducting case studies of thoughtfully
          designed spaces, focusing on their construction methods, material choices, and zoning strategies. I strongly
          believe that collaboration, empathy, and attentive listening are key to developing meaningful architectural
          solutions.
        </p>
      </article>
    </>
  );
};

export default About;
