'use client';
import React, { useRef, useState, useEffect } from 'react';
import { useScroll, useTransform, motion } from 'framer-motion';
import './ParallaxGallery.css';

const ParallaxGallery = () => {
  const gallery = useRef(null);
  const [dimension, setDimension] = useState({ width: 0, height: 0 });

  const { scrollYProgress } = useScroll({
    target: gallery,
    offset: ['start end', 'end start'],
  });

  const { height } = dimension;

  // Define parallax effects for each column
  const y1 = useTransform(scrollYProgress, [0, 1], [0, height * 2]);
  const y2 = useTransform(scrollYProgress, [0, 1], [0, height * 3.3]);
  const y3 = useTransform(scrollYProgress, [0, 1], [0, height * 1.25]);
  const y4 = useTransform(scrollYProgress, [0, 1], [0, height * 3]);

  useEffect(() => {
    const resize = () => {
      setDimension({ width: window.innerWidth, height: window.innerHeight });
    };

    window.addEventListener('resize', resize);
    resize(); // Initialize dimensions

    return () => {
      window.removeEventListener('resize', resize);
    };
  }, []);

  const images = [
    '/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/1.png',
    '/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/1.png',
    '/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/1.png',
    '/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/1.png',
    '/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/1.png',
    '/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/1.png',
    '/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/1.png',
    '/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/1.png',
  ];

  return (
    <main className="parallax-gallery">
      <div className="spacer"></div>
      <div ref={gallery} className="gallery">
        <div className="gallery-wrapper">
          <Column images={images.slice(0, 2)} y={y1} />
          <Column images={images.slice(2, 4)} y={y2} />
          <Column images={images.slice(4, 6)} y={y3} />
          <Column images={images.slice(6, 8)} y={y4} />
        </div>
      </div>
      <div className="spacer"></div>
    </main>
  );
};

const Column = ({ images, y }) => {
  return (
    <motion.div className="gallery-column" style={{ y }}>
      {images.map((src, index) => (
        <div key={index} className="gallery-image">
          <img src={src} alt={`Gallery Image ${index + 1}`} />
        </div>
      ))}
    </motion.div>
  );
};

export default ParallaxGallery;
