import React, { useRef } from "react";
import { motion, useScroll, useTransform } from "framer-motion";
import "/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/components/ZoomParallax.module.css";

// Import your images
import picture1 from "../assets/1.png";
import picture2 from "../assets/2.png";
import picture3 from "../assets/3.JPG";
import picture4 from "../assets/4.jpeg";

const ZoomParallax = () => {
  const containerRef = useRef(null);

  const { scrollYProgress } = useScroll({
    target: containerRef,
    offset: ["start start", "end end"],
  });

  const scales = [
    useTransform(scrollYProgress, [0, 1], [1, 3]),
    useTransform(scrollYProgress, [0, 1], [1, 2.5]),
    useTransform(scrollYProgress, [0, 1], [1, 2]),
    useTransform(scrollYProgress, [0, 1], [1, 1.5]),
  ];

  const images = [picture1, picture2, picture3, picture4];

  return (
    <div ref={containerRef} className="zoom-parallax-container">
      <div className="zoom-parallax-wrapper">
        {images.map((src, index) => (
          <motion.div
            key={index}
            style={{ scale: scales[index] }}
            className="zoom-parallax-element"
          >
            <div className="zoom-parallax-image-container">
              <img src={src} alt={`Zoom Parallax ${index + 1}`} />
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default ZoomParallax;
