import React, { useEffect } from "react";
import "./About.css";
import Navbar from "./Navbar";
import heroImage from "/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/4.jpeg"; // Ensure the correct path
import gsap from "gsap";

const About = () => {
  useEffect(() => {
    // Animation for the hero section
    gsap.from(".about-header-content", {
      opacity: 0,
      y: 50,
      duration: 1.5,
      ease: "power2.out",
    });
  }, []);

  return (
    <>
      <header className="about-header">
        <Navbar />
        <div
          className="about-header-background"
          style={{
            backgroundImage: `url(${heroImage})`,
          }}
        ></div>
        <div className="about-header-content">
          <p className="about-header-intro">Hi, I'm</p>
          <h1 className="about-header-name">Deepa Devangmath</h1>
        </div>
      </header>

      <main className="about-main">
        <section className="about-intro">
          <h2>Introduction</h2>
          <p>
            Hi, I am a licensed architect with a passion for creating meaningful
            architectural solutions. I believe in harmony between context, built
            form, and users, and my research delves into how space impacts human
            behavior and mental well-being.
          </p>
          <p>
            I have actively collaborated on various live projects and take great
            interest in conducting case studies of thoughtfully designed spaces,
            focusing on their construction methods, material choices, and zoning
            strategies.
          </p>
        </section>
      </main>
    </>
  );
};

export default About;
