import React from "react";
import "./About.css";
import profileImage from "./assets/deepa-profile.jpg"; // Ensure the correct path to the image

const About = () => {
  return (
    <section className="about-container">
      <div className="about-content">
        <h1 className="about-title">
          Hello! I'm <span className="about-name">Deepa Devangmath</span>
        </h1>
        <img
          src={profileImage}
          alt="Deepa Devangmath"
          className="about-profile-image"
        />
        <p className="about-description">
          I'm a recent architecture graduate with a deep passion for designing
          spaces that connect people and evoke emotion. To me, architecture is
          about more than just structures—it's about crafting environments that
          resonate with the way people live, feel, and interact. I believe in
          creating spaces that inspire and bring out the best in those who
          experience them. Eager to learn and adapt, I’ve always been quick to
          grasp new concepts and apply them effectively. I’m excited to explore
          new ideas, challenge conventions, and contribute to projects that
          make a meaningful impact on how we connect with the world around us.
        </p>
      </div>
    </section>
  );
};

export default About;
