import React, { useEffect } from "react";
import "./About.css";
import Navbar from "./Navbar";
import gsap from "gsap";
import heroImage from "/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/6.png"; // Correct path to hero image
import profileImage from "/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/3.JPG"; // Correct path to profile image

const About = () => {
  useEffect(() => {
    // GSAP Animations for Hero Text
    gsap.from(".about-hero-text", {
      opacity: 0,
      y: 30,
      duration: 1.5,
      ease: "power2.out",
    });

    // GSAP Animations for Second Section
    gsap.from(".about-second-section-content", {
      opacity: 0,
      x: -50,
      duration: 1.5,
      ease: "power2.out",
      delay: 0.5,
    });
    gsap.from(".about-second-section-image", {
      opacity: 0,
      x: 50,
      duration: 1.5,
      ease: "power2.out",
      delay: 0.5,
    });
  }, []);

  return (
    <>
      {/* Hero Section */}
      <header className="about-hero-section">
        <Navbar />
        <div className="about-hero-image-container">
          <img
            src={heroImage}
            alt="Hero Background"
            className="about-hero-image"
          />
        </div>
        <div className="about-hero-text">
          <h1>Deepa Devangmath</h1>
          <p>Architect | Urban Designer | Interior Specialist</p>
        </div>
      </header>

      {/* Second Section */}
      <section className="about-second-section">
        <div className="about-second-section-content">
          <h2>Introduction</h2>
          <p>
            I'm a recent architecture graduate with a deep passion for designing spaces
            that connect people and evoke emotion. To me, architecture is about crafting
            environments that resonate with the way people live, feel, and interact.
          </p>
          <p>
            I believe in creating spaces that inspire and bring out the best in those
            who experience them. Eager to learn and adapt, Iâ€™ve always been quick to
            grasp new concepts and apply them effectively.
          </p>
        </div>
        <div className="about-second-section-image-container">
          <img
            src={profileImage}
            alt="Deepa Profile"
            className="about-second-section-image"
          />
        </div>
      </section>
    </>
  );
};

export default About;
