import React, { useEffect } from "react";
import "./About.css";
import Navbar from "./Navbar";
import gsap from "gsap";

const About = () => {
  useEffect(() => {
    // Animation for hero and second section
    gsap.from(".about-hero-content", {
      opacity: 0,
      y: 50,
      duration: 1.5,
      ease: "power2.out",
    });
    gsap.from(".about-second-section", {
      opacity: 0,
      y: 50,
      duration: 1.5,
      ease: "power2.out",
      delay: 0.5,
    });
  }, []);

  return (
    <>
      <header className="about-hero">
        <Navbar />
        <div className="about-hero-content">
          <p className="about-hero-intro">Hi, I'M</p>
          <h1 className="about-hero-title">Deepa Devangmath</h1>
        </div>
      </header>

      <main className="about-main">
        {/* Second Section */}
        <section className="about-second-section">
          <div className="about-second-image-container">
            <img
              src="/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/3.JPG" // Replace with the correct path
              alt="Deepa Devangmath"
              className="about-second-image"
            />
          </div>
          <div className="about-second-text">
            <p>
              I'm a recent architecture graduate with a deep passion for
              designing spaces that connect people and evoke emotion. To me,
              architecture is about more than just structures—it's about
              crafting environments that resonate with the way people live,
              feel, and interact.
            </p>
            <p>
              I believe in creating spaces that inspire and bring out the best
              in those who experience them. Eager to learn and adapt, I’ve
              always been quick to grasp new concepts and apply them
              effectively. I’m excited to explore new ideas, challenge
              conventions, and contribute to projects that make a meaningful
              impact on how we connect with the world around us.
            </p>
          </div>
        </section>
      </main>
    </>
  );
};

export default About;
