import React, { useEffect } from "react";
import { gsap } from "gsap";
import "./Loader.css"; // Add your styles here

const Loader = () => {
  useEffect(() => {
    const tl = gsap.timeline({ paused: true });

    tl.set(".loader", {
      display: "block",
    })
      .set(".loader__element", {
        transformOrigin: "center right",
      })
      .to(".loader__element", {
        scaleX: 1,
        duration: 0.6,
        ease: "expo.inOut",
        stagger: 0.1,
      })
      .set(".loader__element", {
        transformOrigin: "center left",
      })
      .to(".loader__element", {
        scaleX: 0,
        duration: 0.6,
        ease: "expo.inOut",
        stagger: -0.1,
      })
      .set(".loader", {
        display: "none",
      });

    document.addEventListener("click", () => tl.play(0));

    return () => {
      document.removeEventListener("click", () => tl.play(0));
    };
  }, []);

  return (
    <div className="loader">
      <div className="loader__element"></div>
      {/* Uncomment below for stagger curtain effect */}
      {/* <div className="loader__element"></div> */}
    </div>
  );
};

export default Loader;
