import "./About.css";
import profileImage from "/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/assets/3.JPG"; // Update path to your image
import Navbar from "/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/components/Navbar.js";

const About = () => {
  return (
    <div className="about-page">
      <Navbar />
      {/* Hero Section */}
      <div className="about-hero">
        <div className="about-hero-content">
          <h1 className="about-hero-title">About Me</h1>
          <h2 className="about-hero-subtitle">UX/UI Designer</h2>
          <p className="about-hero-description">
            In my work, I blend minimalism with a creative approach,
            enabling a focus on what matters while highlighting the value
            of your offering and opening up new opportunities for growth.
          </p>
          <div className="about-hero-button">
            <button>Letâ€™s Connect</button>
          </div>
        </div>
        <div className="about-hero-image-container">
          <img src={profileImage} alt="Polina K" className="about-hero-image" />
        </div>
      </div>
    </div>
  );
};

export default About;
