import React, { useRef } from "react";
import { motion, useScroll, useTransform } from "framer-motion";
import Image from "deepa-portfolio/public/images"; // If Next.js is used
import Picture1 from "deepa-portfolio/public/images/1.jpeg";
import Picture2 from "deepa-portfolio/public/images/1.jpeg";
import Picture3 from "deepa-portfolio/public/images/1.jpeg";
import Picture4 from "deepa-portfolio/public/images/1.jpeg";
import Picture5 from "deepa-portfolio/public/images/1.jpeg";
import Picture6 from "deepa-portfolio/public/images/1.jpeg";
import Picture7 from "deepa-portfolio/public/images/1.jpeg";
import "/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/components/ZoomParallax.module.css"; // Adjust if using SCSS

const ZoomParallax = () => {
  const container = useRef(null);
  const { scrollYProgress } = useScroll({
    target: container,
    offset: ["start start", "end end"],
  });

  const scaleArray = [
    useTransform(scrollYProgress, [0, 1], [1, 4]),
    useTransform(scrollYProgress, [0, 1], [1, 5]),
    useTransform(scrollYProgress, [0, 1], [1, 6]),
    useTransform(scrollYProgress, [0, 1], [1, 8]),
    useTransform(scrollYProgress, [0, 1], [1, 9]),
  ];

  const pictures = [
    { src: Picture1, scale: scaleArray[0] },
    { src: Picture2, scale: scaleArray[1] },
    { src: Picture3, scale: scaleArray[2] },
    { src: Picture4, scale: scaleArray[1] },
    { src: Picture5, scale: scaleArray[2] },
    { src: Picture6, scale: scaleArray[3] },
    { src: Picture7, scale: scaleArray[4] },
  ];

  return (
    <div ref={container} className="container">
      <div className="sticky">
        {pictures.map(({ src, scale }, index) => (
          <motion.div key={index} style={{ scale }} className="el">
            <div className="image-container">
              <Image
                src={src}
                fill
                alt={`Zoom Parallax ${index + 1}`}
                placeholder="blur"
              />
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default ZoomParallax;
