import React, { useRef } from "react";
import { motion, useScroll, useTransform } from "framer-motion";
import styles from "deepa-portfolio/src/components/styles.module.scss";
import picture1 from "/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/images/1.jpeg";
import picture2 from "/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/images/1.jpeg";
import picture3 from "/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/images/1.jpeg";
import picture4 from "/Users/aniruddhkapileshwari/Desktop/Deepa-pot/deepa-portfolio/src/images/1.jpeg";

const ZoomParallax = () => {
  const containerRef = useRef(null);
  const { scrollYProgress } = useScroll({
    target: containerRef,
    offset: ["start start", "end end"],
  });

  const scales = [
    useTransform(scrollYProgress, [0, 1], [1, 3]),
    useTransform(scrollYProgress, [0, 1], [1, 2.5]),
    useTransform(scrollYProgress, [0, 1], [1, 2]),
    useTransform(scrollYProgress, [0, 1], [1, 1.5]),
  ];

  const images = [picture1, picture2, picture3, picture4];

  return (
    <div ref={containerRef} className={styles.container}>
      <div className={styles.sticky}>
        {images.map((src, index) => (
          <motion.div
            key={index}
            style={{ scale: scales[index] }}
            className={styles.element}
          >
            <div className={styles.imageContainer}>
              <img src={src} alt={`Zoom Parallax ${index + 1}`} />
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default ZoomParallax;
