import React, { useRef } from "react";
import { motion, useScroll, useTransform } from "framer-motion";
import picture1 from "../images/1.jpeg";
import picture2 from "../images/2.jpeg";
import picture3 from "../images/3.jpg";
import picture4 from "../images/4.jpg";
import picture5 from "../images/5.jpg";
import Picture6 from "../images/6.jpg";
import Picture7 from "../images/7.jpeg";
import styles from  "./ZoomParallax.module.css";

const pictures = [
    { src: picture1, scaleRange: [1, 4] },
    { src: picture2, scaleRange: [1, 3.5] },
    { src: picture3, scaleRange: [1, 3] },
    { src: picture4, scaleRange: [1, 2.5] },
  ];

  const ZoomParallax = () => {
    const containerRef = useRef(null);
    const { scrollYProgress } = useScroll({
      target: containerRef,
      offset: ["start start", "end end"],
    });

    // Pre-compute scale values for each image
    const scales = pictures.map((picture) =>
      useTransform(scrollYProgress, [0, 1], picture.scaleRange)
    );

    return (
      <div ref={containerRef} className={styles.container}>
        <div className={styles.sticky}>
          {pictures.map((picture, index) => (
            <motion.div
              key={index}
              style={{ scale: scales[index] }}
              className={styles.el}
            >
              <div className={styles.imageContainer}>
                <img src={picture.src} alt={`Zoom Parallax ${index + 1}`} />
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    );
  };

  export default ZoomParallax;
