import React, { useState } from 'react';
import './Home.css';

const images = [
    "https://via.placeholder.com/1200x900",
    "https://via.placeholder.com/1200x901",
    "https://via.placeholder.com/1200x902"
];

const Home = () => {
    const [currentIndex, setCurrentIndex] = useState(0);

    const handleNext = () => {
        setCurrentIndex((currentIndex + 1) % images.length);
    };

    const handlePrev = () => {
        setCurrentIndex((currentIndex - 1 + images.length) % images.length);
    };

    return (
        <div className="homepage">
            {/* Sidebar */}
            <div className="sidebar">
                <h1>Deepa Devangmath</h1>
                <h2>Architecture</h2>
                <nav>
                    <ul>
                        <li><a href="#home">Home</a></li>
                        <li><a href="#about">About</a></li>
                        <li><a href="#blog">Blog</a></li>
                        <li><a href="#publications">Publications & Awards</a></li>
                        <li><a href="#contact">Contact</a></li>
                        <li><a href="#works">Selected Works</a></li>
                    </ul>
                </nav>
                <div className="awards">
                    <p><strong>Winner</strong></p>
                    <p>Dezeen Awards 2018</p>
                    <p>Architect of the Year</p>
                    <p>Awards Finalist 2018</p>
                </div>
            </div>

            {/* Hero Section */}
            <div className="hero">
                <img src={images[currentIndex]} alt="Hero background" />
                <div className="carousel-controls">
                    <span onClick={handlePrev}>&lt;</span>
                    <span>{currentIndex + 1}/{images.length}</span>
                    <span onClick={handleNext}>&gt;</span>
                </div>
            </div>
        </div>
    );
};

export default Home;
